function getDiscussion(body) {
  return {
    discussionId: Date.now(),
    title: body ? body.title : 'discussion title',
    status: 1,
    selectionStart: body ? body.selectionStart : 0,
    selectionEnd: body ? body.selectionEnd : 0,
    messagesCount: Math.floor(Math.random() * Math.floor(20)),
  };
}

export default (req: NextApiRequest, res: NextApiResponse): void => {
  const story = stories[req.query.slug[0]];
  switch (req.method) {
    case 'GET':
      if (story) {
        res.status(200).json(story);
      } else {
        res.status(404).json({ error: 'Not found' });
      }
      break;
    case 'POST':
      if (req.query.slug[1] === 'discuss') {
        res.status(200).json(getDiscussion(req.body));
      } else {
        res.status(404).json({ error: 'Not found' });
      }
      break;
    default:
      res.setHeader('Allow', ['GET, POST']);
      res.status(405).end(`Method ${req.method} Not Allowed`);
  }
};
